INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('63991ede-eb65-45ee-8ed4-683ec1233ea0', 'AWS AK/SK 数量检测', 1, 'HighRisk', 'AK/SK是重要的访问凭证，一般情况下建议用户只有一个，如果有两个可能存在风险。拥有2个激活的AK/SK视为”不合规“，否则视为”合规“', 'policies:\n  #  AK/SK是重要的访问凭证，一般情况下建议用户只有一个，如果有两个可能存在风险。拥有2个激活的AK/SK视为”不合规“，否则视为”合规“\n  - name: aws-iam-user-two-active-access-key\n    resource: aws.iam-user\n    filters:\n      - type: credential\n        key: access_keys.active\n        value: true\n      - type: credential\n        key: access_keys\n        value_type: size\n        value: 2\n        op: eq', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('2ec8bf6d-49e2-4751-9a67-39b17522529f', 'AWS IAM 非必要的access key检测', 1, 'LowRisk', '在创建账号时可能会错误的勾选编程方式访问，我们需要验证是否存在非必要的access key存在，同时满足：开启密码登录，存在AK和AK没有被使用过，视为”不合规“，否则视为”合规“', 'policies:\n  # 在创建账号时可能会错误的勾选编程方式访问，我们需要验证是否存在非必要的access key存在，同时满足：开启密码登录，存在AK和AK没有被使用过，视为”不合规“，否则视为”合规“\n  - name: aws-iam-user-no-setup-initial-access-key\n    resource: aws.iam-user\n    filters:\n      - type: credential\n        key: access_keys.active\n        value: true\n      - type: credential\n        key: access_keys.last_used_date\n        value: null\n        op: eq\n      - type: credential\n        key: password_enabled\n        value: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('2eb58a78-2b48-43c1-85cc-a42d6240bec9', 'AWS IAM 用户多重身份验证 (MFA)检测', 1, 'LowRisk', '确保为所有具有控制台密码的 IAM 用户启用多重身份验证 (MFA)，如果开启控制台密码登录并使用MFA 视为”合规“，否则视为”不合规“', 'policies:\n  # 确保为所有具有控制台密码的 IAM 用户启用多重身份验证 (MFA)，如果开启控制台密码登录并使用MFA 视为”合规“，否则视为”不合规“\n  - name: aws-iam-user-mfa-enabled-console-access\n    resource: aws.iam-user\n    filters:\n      - type: credential\n        key: password_enabled\n        value: true\n      - type: credential\n        key: mfa_active\n        value: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a787ffbc-6d7e-4546-b9bb-850eeadd363a', 'AWS Support 管理检测', 1, 'LowRisk', '确保已创建支持角色来通过 AWS Support 管理事件，存在视为”合规“，否则视为”不合规“', 'policies:\n  #  确保已创建支持角色来通过 AWS Support 管理事件，存在视为”合规“，否则视为”不合规“\n  - name: aws-iam-support-role-created\n    resource: aws.iam-role\n    filters:\n      - type: has-specific-managed-policy\n        value: AWSSupportServiceRolePolicy', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('cadef8ae-66d9-4716-9c76-7c7c057b4b0b', 'AWS AK/SK 检测', 1, 'CriticalRisk', 'AK/SK是重要的访问凭证，一般情况下需要用户定期轮换所有访问密钥。如果没有开启定期轮换或轮换周期大于90天视为”不合规“，否则视为”合规“', 'policies:\n  #  AK/SK是重要的访问凭证，一般情况下需要用户定期轮换所有访问密钥。如果没有开启定期轮换或轮换周期大于90天视为”不合规“，否则视为”合规“\n  - name: aws-iam-rotate-access-key-90-days\n    resource: aws.iam-user\n    filters:\n      - type: credential\n        key: access_keys.active\n        value: true\n      - type: credential\n        key: access_keys.last_rotated\n        value_type: age\n        value: 90\n        op: gt\n      - type: credential\n        key: access_keys.last_rotated\n        value: null\n        op: ne', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('f23b5622-4ba8-4db1-8605-e0c8c654a521', 'AWS root 帐户启用 MFA 检测', 1, 'HighRisk', '确保为 root 帐户启用 MFA，未激活 MFA 视为”不合规“，否则视为”合规“', 'policies:\n  #  确保为 root 帐户启用 MFA，未激活 MFA 视为”不合规“，否则视为”合规“\n  - name: aws-iam-root-mfa-enabled\n    resource: aws.account\n    filters:\n      - type: iam-summary\n        key: AccountMFAEnabled\n        value: false\n        op: eq\n        value_type: swap', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('366da429-3975-4323-b499-88d24bb75bd4', 'AWS IAM “cloudtrail:*”权限检测', 1, 'MediumRisk', '确保不存在“cloudtrail:*”权限的 IAM 策略，存在视为”不合规“，否则视为”合规“', 'policies:\n  # 确保不存在“cloudtrail:*”权限的 IAM 策略，存在视为”不合规“，否则视为”合规“\n  - name: aws-iam-policy-no-full-access-to-cloudtrail\n    resource: aws.iam-policy\n    filters:\n      - type: check-permissions\n        match: allowed\n        actions:\n          - \'cloudtrail:*\'', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a31dca86-efce-4e42-ae31-25ae42570d72', 'AWS IAM “*:*”管理权限检测', 1, 'MediumRisk', '确保不存在“*:*”管理权限的 IAM AWS 托管策略，存在视为”不合规“，否则视为”合规“', 'policies:\n  #  确保不存在“*:*”管理权限的 IAM AWS 托管策略，存在视为”不合规“，否则视为”合规“\n  - name: aws-iam-policy-no-administrative-privileges\n    resource: aws.iam-policy\n    filters:\n      - type: used\n      - type: has-allow-all', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('4ef097ba-825d-4a60-87a3-d5313960c6db', 'AWS IAM 统一权限检测', 1, 'MediumRisk', '仅通过组分配 IAM 策略,可将权限管理统一到与组织功能角色一致的单个组中。 通过统一权限管理，降低权限过多的可能性。存在不通过组来授权的IAM策略视为”不合规“，否则视为”合规“', 'policies:\n  #  仅通过组分配 IAM 策略,可将权限管理统一到与组织功能角色一致的单个组中。 通过统一权限管理，降低权限过多的可能性。存在不通过组来授权的IAM策略视为”不合规“，否则视为”合规“\n  - name: aws-iam-policy-attached-only-to-group-or-roles\n    resource: aws.iam-user\n    filters:\n      - type: has-inline-policy\n        value: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('7c3d1a1f-eaab-4937-9a30-1c53ab2c865a', 'AWS IAM 密码重复检测', 1, 'HighRisk', '确保 IAM 密码策略防止密码重复使用，需要设置不小于24，同时要确认已开启密码策略配置。满足视为”合规“，否则为”不合规“', 'policies:\n  #  确保 IAM 密码策略防止密码重复使用，需要设置不小于24，同时要确认已开启密码策略配置。满足视为”合规“，否则为”不合规“\n  - name: aws-iam-password-policy-reuse-24\n    resource: aws.account\n    filters:\n    - or:\n      - type: password-policy\n        key: PasswordPolicyConfigured\n        value: false\n    - or:\n      - type: password-policy\n        key: PasswordReusePrevention\n        value: 24\n        op: lt', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('3304336b-6bce-411a-9c32-a9bb4993ea7f', 'AWS IAM 密码符号检测', 1, 'HighRisk', 'IAM 密码策略检查，需要包含特殊符号，大写字母，小写字母，符合视为”合规“，否则视为”不合规“', 'policies:\n  #  IAM 密码策略检查，需要包含特殊符号，大写字母，小写字母，符合视为”合规“，否则视为”不合规“\n  - name: aws-iam-password-policy-require-symbol\n    resource: aws.account\n    filters:\n    - or:\n      - type: password-policy\n        key: PasswordPolicyConfigured\n        value: false\n    - or:\n      - type: password-policy\n        key: RequireSymbols\n        value: true\n      - type: password-policy\n        key: RequireLowercaseCharacters\n        value: true\n      - type: password-policy\n        key: RequireUppercaseCharacters\n        value: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('7782b78c-def7-4265-abb2-43624d31a0d3', 'AWS IAM 密码长度检测', 1, 'HighRisk', 'IAM 密码策略的长度不能少于14，长度小于14视为”不合规“，否则视为”合规“', 'policies:\n  #  IAM 密码策略的长度不能少于14，长度小于14视为”不合规“，否则视为”合规“\n  - name: aws-iam-password-policy-minimum-length-14\n    resource: aws.account\n    filters:\n    - or:\n      - type: password-policy\n        key: MinimumPasswordLength\n        value: 14\n        op: lt\n      - type: password-policy\n        key: PasswordPolicyConfigured\n        value: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('3a21ffd1-bce4-40de-a939-27d942dc837f', 'AWS IAM 密码时间检测', 1, 'MediumRisk', '确保 IAM 密码使用时间不能超过某一时间，超期时间小于90天视为”不合规“，否则视为”合规“', 'policies:\n  #  确保 IAM 密码使用时间不能超过某一时间，超期时间小于90天视为”不合规“，否则视为”合规“\n  - name: aws-iam-password-policy-expires-passwords\n    resource: aws.account\n    filters:\n    - or:\n      - type: password-policy\n        key: MaxPasswordAge\n        value: 90\n        op: lt\n      - type: password-policy\n        key: PasswordPolicyConfigured\n        value: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('90b44294-a47f-43bc-901f-8649a9db6e89', 'AWS 根帐户访问密钥检测', 1, 'MediumRisk', '确保不存在根帐户访问密钥，不是root账户或不存在密钥视为”合规“，否则视为”不合规“', 'policies:\n  #  确保不存在根帐户访问密钥，不是root账户或不存在密钥视为”合规“，否则视为”不合规“\n  - name: aws-iam-no-root-access-key\n    resource: aws.account\n    filters:\n      - type: credential\n        key: access_keys\n        value: absent', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('73cc9b5d-a891-40b3-bcbb-faf07e4229ed', 'AWS IAM SSL/TLS 证书检测', 1, 'MediumRisk', '确保删除 AWS IAM 中存储的所有过期 SSL/TLS 证书，存在过期证书视为”不合规“，否则视为”合规“', 'policies:\n  #  确保删除 AWS IAM 中存储的所有过期 SSL/TLS 证书，存在过期证书视为”不合规“，否则视为”合规“\n  - name: aws-iam-no-expired-server-certificates-stored\n    resource: aws.iam-certificate\n    filters:\n      - type: value\n        key: Expiration\n        value_type: expiration\n        op: gt\n        value: 0', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('ce9b31e8-9e81-4321-9e8f-97169432083f', 'AWS IAM 未使用凭据检测', 1, 'MediumRisk', '建议停用或删除 45 天内未使用的所有凭据。存在访问凭证超过45天未使用视为”不合规“，否则视为”合规“', 'policies:\n  #  建议停用或删除 45 天内未使用的所有凭据。存在访问凭证超过45天未使用视为”不合规“，否则视为”合规“\n  - name: aws-iam-disable-45-days-credentials\n    resource: aws.iam-user\n    filters:\n    - or:\n      - type: credential\n        key: access_keys.last_used_date\n        value_type: age\n        value: 45\n        op: gt\n      - type: credential\n        key: access_keys.last_used_date\n        value: null\n        op: eq', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('c1f26a59-606e-4bad-a4b2-e595ec95bd0c', 'AWS IAM SAML 提供商检测', 1, 'MediumRisk', '检查是否有 SAML 提供商，存在视为”合规“，否则视为”不合规“', 'policies:\n  #  检查是否有 SAML 提供商，存在视为”合规“，否则视为”不合规“\n  - name: aws-iam-check-saml-providers\n    resource: aws.iam-saml-provider', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('0b2dd583-c6dc-4417-8334-9cda0b69d326', 'AWS IAM root账号检测', 1, 'MediumRisk', '应避免使用root账号，即root账户没有通过密码登录，也没有通过密钥访问，没有访问视过为”合规“，否则视为”不合规“', 'policies:\n  #  应避免使用root账号，即root账户没有通过密码登录，也没有通过密钥访问，没有访问视过为”合规“，否则视为”不合规“\n  - name: aws-iam-avoid-root-usage\n    resource: aws.account\n    filters:\n    - type: credential\n      key: password_last_used\n      value: null\n      op: ne', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('0e991cfd-dbd6-4085-b10a-1219a681e775', 'AWS IAM 角色检测', 1, 'MediumRisk', '确保已创建审计角色来执行安全审计，存在视为”合规“，否则视为”不合规“', 'policies:\n  #  确保已创建审计角色来执行安全审计，存在视为”合规“，否则视为”不合规“\n  - name: aws-iam-support-role-created\n    resource: aws.iam-role\n    filters:\n      - type: has-specific-managed-policy\n        value: SecurityAudit', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('473a5d13-9987-4d34-8adb-2e869d7bb7ad', 'AWS IAM 访问分析器检测', 1, 'MediumRisk', '检查 IAM 访问分析器是否已启用，开启视为”合规“，否则视为”不合规“', 'policies:\n  #  检查 IAM 访问分析器是否已启用，开启视为”合规“，否则视为”不合规“\n  - name: aws-accessanalyzer-enabled\n    resource: aws.iam-role\n    filters:\n      - type: iam-analyzer\n        key: \'status\'\n        value: ACTIVE', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('8b423c17-6f93-4c0e-a65f-23f1d7958095', 'AWS ALB WAF ACL检测', 1, 'LowRisk', '检查应用程序负载均衡器是否附加了 WAF ACL，有附加视为“合规”，否则视为“不合规”', 'policies:\n  #  检查应用程序负载均衡器是否附加了 WAF ACL，有附加视为“合规”，否则视为“不合规”\n  - name: aws-elbv2-waf-acl-attached\n    resource: aws.app-elb\n    filters:\n    - or:\n      - type: waf-enabled\n        state: false\n      - type: wafv2-enabled\n        state: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a7716e26-eb79-4199-b349-4e7ca2f29ad7', 'AWS ALB SSL 监听器检测', 1, 'LowRisk', '检查 ALB 弹性负载均衡器是否有 SSL 监听器，有视为“合规”，否则视为“不合规”', 'policies:\n  #  检查 ALB 弹性负载均衡器是否有 SSL 监听器，有视为“合规”，否则视为“不合规”\n  - name: aws-elbv2-ssl-listeners\n    resource: aws.app-elb\n    filters:\n      - type: listener\n        key: Protocol\n        value: [HTTPS,SSL,TSL]\n        op: ni', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('fe89c593-342e-4640-b358-6b661323641f', 'AWS ALB 日志记录检测', 1, 'LowRisk', '检查弹性负载均衡器是否启用了日志记录。启动视为“合规”，否则视为“不合规”', 'policies:\n  #  检查弹性负载均衡器是否启用了日志记录。启动视为“合规”，否则视为“不合规”。\n  - name: aws-elbv2-logging-enabled\n    resource: aws.app-elb\n    filters:\n      - type: is-not-logging', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a1dfd261-0746-4969-be62-4b07755dcc07', 'AWS ALB 监听器检测', 1, 'LowRisk', '检查 ALB 下面是否有监听器，没有视为“不合规”，否则视为“合规”', 'policies:\n  #  检查 ALB 下面是否有监听器，没有视为“不合规”，否则视为“合规”\n  - name: aws-elbv2-listeners-underneath\n    resource: aws.app-elb\n    filters:\n      - type: listener\n        key: Protocol\n        value: [HTTPS,HTTP]\n        op: in\n      - type: listener\n        value_type: resource_count  \n        op: eq\n        value: 1\n        matched: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('231fb1cb-e673-4cdc-8dd9-dc921a9c8848', 'AWS ALB 互联网访问检测', 1, 'LowRisk', '检查 ALB 是否可以从互联网进行访问，可以视为“不合规”，否则视为“合规”', 'policies:\n  #  检查 ALB 是否可以从互联网进行访问，可以视为“不合规”，否则视为“合规”\n  - name: aws-elb-no-instance\n    resource: aws.app-elb\n    filters:\n      - type: value\n        key: Scheme\n        value: internet-facing\n        op: eq', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('e3a13f7e-0b1b-4ba7-8a4b-48a78bf45a33', 'AWS ELB 健康检查检测', 1, 'LowRisk', '检查 ELB 使用的协议和健康检查协议是否一致，一致视为“合规”，否则视为“不合规”', 'policies:\n  #  检查 ELB 使用的协议和健康检查协议是否一致，一致视为“合规”，否则视为“不合规”\n  - name: aws-elbv2-healthcheck-mismatch\n    resource: aws.app-elb\n    filters:\n      - type: healthcheck-protocol-mismatch', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a1210f8c-cceb-4df3-85b6-1a3861ccfc10', 'AWS ELB 异步缓解模式检测', 1, 'LowRisk', '检查应用程序负载均衡器是否配置了防御或最严格的异步缓解模式，如果没有，请检查是否至少配置了 drop_invalid_header_fields 属性，没有视为“不合规”，否则视为“合规”', 'policies:\n  #  检查应用程序负载均衡器是否配置了防御或最严格的异步缓解模式，如果没有，请检查是否至少配置了 drop_invalid_header_fields 属性，没有视为“不合规”，否则视为“合规”\n  - name: aws-elbv2-desync-mitigation-mode\n    resource: aws.app-elb\n    filters:\n      - type: attributes\n        key: routing.http.desync_mitigation_mode\n        value: [\'strictest\',\'defensive\']\n        op: not-in', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
