INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('bc4b5ad9-a8cb-46da-bbfe-ca29e182eae4', 'AWS EBS 快照设置检测', 1, 'MediumRisk', '确保没有 EBS 快照设置为公共。有共有快照为为”不合规“，否则视为”合规“', 'policies:\n  #  确保没有 EBS 快照设置为公共。有共有快照为为”不合规“，否则视为”合规“\n  - name: aws-EC2-ebs-public-snapshot\n    resource: aws.ebs-snapshot\n    filters:\n      - type: cross-account\n        everyone_only: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
