INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('443c04aa-9133-47b8-96b5-da22ff95ad65', 'AWS 存储桶是否启用了 ACL 检测', 1, 'MediumRisk', '检查 S3 存储桶是否启用了 ACL。开启视为”不合规“，否则视为”合规“。', 'policies:\n  #  检查 S3 存储桶是否启用了 ACL。开启视为”不合规“，否则视为”合规“。\n  - name: aws-s3-bucket-acl-prohibited\n    resource: aws.s3\n    filters:\n      - type: ownership\n        value: BucketOwnerEnforced', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('925f698a-e821-45f0-b92a-c3b0c95efd1a', 'AWS  存储桶 MFA 删除启用检测', 1, 'MediumRisk', '检查 S3 存储桶 MFA 删除是否未启用。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查 S3 存储桶 MFA 删除是否未启用。开启视为”合规“，否则视为”不合规“。\n  - name: aws-s3-bucket-no-mfa-delete\n    resource: aws.s3\n    filters:\n    - or:\n      - type: value\n        key: Versioning.MFADelete\n        value: false\n      - type: value\n        key: Versioning.MFADelete\n        value: absent\n      - type: value\n        key: Versioning.MFADelete\n        value: \'Disabled\'', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('0c58d1ec-1621-4a90-b0ce-57ea782639f6', 'AWS 存储桶启用服务器访问日志记录检测', 1, 'MediumRisk', '检查 S3 存储桶是否启用了服务器访问日志记录。启用视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查 S3 存储桶是否启用了服务器访问日志记录。启用视为”合规“，否则视为”不合规“。\n  - name: aws-s3-bucket-server-access-logging-enabled\n    resource: aws.s3\n    filters:\n      - type: bucket-logging\n        op: disabled', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('2196d5ba-31ea-4c17-aed3-5973de343315', 'AWS 任何人可以写的存储桶检测', 1, 'CriticalRisk', '确保没有可以被任何人或任何 AWS 客户可写的 S3 存储桶。存在视为”不合规“，否则视为”合规“。', 'policies:\n  #  确保没有可以被任何人或任何 AWS 客户可写的 S3 存储桶。存在视为”不合规“，否则视为”合规“。\n  - name: aws-s3-bucket-public-write-acl\n    resource: aws.s3\n    filters:\n      - type: global-grants\n        allow_website: false\n        permissions: [\'FULL_CONTROL\',\'WRITE_ACP\',\'WRITE\']\n        operator: \'or\'', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('50003d5a-29c6-407e-b86a-5bc8efa25bd0', 'AWS 可以列出全部存储桶检测', 1, 'CriticalRisk', '确保没有可以被任何人或任何 AWS 客户列出的 S3 存储桶。存在视为”不合规“，否则视为”合规“。', 'policies:\n  #  确保没有可以被任何人或任何 AWS 客户列出的 S3 存储桶。存在视为”不合规“，否则视为”合规“。\n  - name: aws-s3-bucket-public-list-acl\n    resource: aws.s3\n    filters:\n      - type: global-grants\n        allow_website: false\n        permissions: [\'FULL_CONTROL\',\'READ_ACP\',\'READ\']\n        operator: \'or\'', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('14028662-049c-4183-88d6-0aeee8804732', 'AWS 存储桶全部开放访问检测', 1, 'CriticalRisk', '确保没有任何 S3 存储桶对所有人或任何 AWS 用户开放。存在视为”不合规“，否则视为”合规“。', 'policies:\n  #  确保没有任何 S3 存储桶对所有人或任何 AWS 用户开放。存在视为”不合规“，否则视为”合规“。\n  - name: aws-s3-bucket-public-access\n    resource: aws.s3\n    filters:\n    - or:\n      - type: global-grants\n      - type: has-statement\n        statements:\n          - Effect: Allow\n            Principal: \'*\'\n      - type: has-statement\n        statements:\n          - Effect: Allow\n            Principal: \n              aws:\'*\'', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('383203ff-3728-4c92-9ab4-bc3360909971', 'AWS 存储桶启用对象版本控制检测', 1, 'MediumRisk', '检查 S3 存储桶是否启用了对象版本控制。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查 S3 存储桶是否启用了对象版本控制。开启视为”合规“，否则视为”不合规“。\n  - name: aws-s3-bucket-object-versioning\n    resource: aws.s3\n    filters:\n    - or:\n      - type: value\n        key: Versioning.Status\n        value: Suspended\n      - type: value\n        key: Versioning.Status\n        value: absent', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('b404ec02-50fa-4e6b-8261-d605471a4ba4', 'AWS 存储桶级公共访问检测', 1, 'MediumRisk', '检查 S3 存储桶级公共访问。可以公共访问视为”不合规“，否则视为”合规“。', 'policies:\n  #  检查 S3 存储桶级公共访问。可以公共访问视为”不合规“，否则视为”合规“。\n  - name: aws-s3-bucket-level-public-access-block\n    resource: aws.s3\n    filters:\n    - or:\n      - type: check-public-block\n        BlockPublicAcls: false\n      - type: check-public-block\n        BlockPublicPolicy: false\n      - type: check-public-block\n        RestrictPublicBuckets: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('c92f9994-178a-447d-9d9c-5fd9097c87ac', 'AWS EC2 AMI 公共检测', 1, 'MediumRisk', '确保没有 EC2 AMI 设置为公共。开启视为”合规“，否则视为”不合规“', 'policies:\n  #  确保没有 EC2 AMI 设置为公共。开启视为”合规“，否则视为”不合规“\n  - name: aws-EC2-ami-public\n    resource: aws.ami\n    filters:\n      - type: value\n        key: Public\n        value: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('2d498283-e305-4b2f-8bba-c8e5cff2867b', 'AWS RDS快照公开检测', 1, 'CriticalRisk', '检查RDS快照快照是否公开。未公开视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查RDS快照快照是否公开。未公开视为”合规“，否则视为”不合规“。\n  #  返回：返回客户端连接未加密的RDS实例。\n  - name: aws-rds-snapshots-public-access\n    resource: aws.rds\n    filters:\n      - type: cross-account\n        everyone_only: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('88c09fc3-dc5e-4635-bd64-4ff6aa7293dd', 'AWS 过期RDS快照检测', 1, 'LowRisk', '检查存在超过了30天的RDS快照。快照存在超过30天视为”不合规“，否则视为”合规“。', 'policies:\n  #  检查存在超过了30天的RDS快照。快照存在超过30天视为”不合规“，否则视为”合规“。\n  #  返回：返回超过制定期限的RDS 快照。\n  - name: aws-rds-snapshots-expired\n    resource: aws.rds-snapshot\n    filters:\n      - type: age\n        days: 30\n        op: ge', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('d0971565-854e-4d7a-a0cd-a77fde2a03e3', 'AWS RDS 孤儿快照检测', 1, 'LowRisk', '检测孤儿快照的存在，也就是没有关联RDS实例的 RDS 快照 ，存在RDS孤儿快照视为”不合规“，否则视为”合规“。', 'policies:\n  #  检测孤儿快照的存在，也就是没有关联RDS实例的 RDS 快照 ，存在RDS孤儿快照视为”不合规“，否则视为”合规“。\n  #  返回：返回全部没有关联RDS实例的RDS孤儿快照\n  - name: aws-rds-snapshot-orphan\n    resource: aws.rds-snapshot\n    filters:\n    - type: instance\n      value: 0\n      value_type: resource_count', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('c544dadb-de3e-45d3-866c-5d2016010b36', 'AWS RDS客户端加密检测', 1, 'HighRisk', '检查 RDS 实例客户端连接是否已加密（Microsoft SQL Server 和 PostgreSQL）。加密视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查 RDS 实例客户端连接是否已加密（Microsoft SQL Server 和 PostgreSQL）。加密视为”合规“，否则视为”不合规“。\n  #  返回：返回客户端连接未加密的RDS实例。\n  - name: aws-rds-instance-transport-encrypted\n    resource: aws.rds\n    filters:\n      - type: db-option-groups\n        key: Options[].OptionName\n        op: intersect\n        value:\n          - SSL\n          - NATIVE_NETWORK_ENCRYPTION', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a7b535d3-52a8-4050-8615-51e65563fd3d', 'AWS RDS 存储加密检测', 1, 'MediumRisk', '如果未启用，静态敏感信息将不受保护。', 'policies:\n  #  检查RDS实例存储是否加密。存储加密视为”合规“，否则视为”不合规“。\n  #  返回：返回为对存储加密的RDS实例。\n  - name: aws-rds-instance-storage-encrypted\n    resource: aws.rds\n    filters:\n      - type: value\n        key: StorageEncrypted\n        value: true\n        op: ne', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('69ac4eb6-25c6-410d-ba1c-dac498b95f60', 'AWS 可公开访问RDS检测', 1, 'CriticalRisk', '可公开访问的数据库可能会将敏感数据暴露给不良行为者。', 'policies:\n  #  确保没有公共可访问的 RDS 实例。不能公开访问视为”合规“，否则视为”不合规“。\n  #  返回：返回可以公开访问的RDS实例。\n  - name: aws-rds-instance-no-public-access\n    resource: aws.rds\n    filters:\n      - PubliclyAccessible: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('a0ac031b-1e8e-41e5-8416-fe8a0b449993', 'AWS RDS 开启多可用区', 1, 'MediumRisk', '如果发生故障，使用单可用区部署配置时，如果发生特定于可用区的数据库故障，Amazon RDS 无法自动故障转移到备用可用区。', 'policies:\n  # 检查RDS实例是否启用了多可用区。开启视为”合规“，否则视为”不合规“。\n  #  返回：返回没有开启的RDS实例。\n  - name: aws-rds-instance-multi-az\n    resource: aws.rds\n    filters:\n      - type: db-parameter\n        key: MultiAZ\n        value: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('3084604a-acef-4da8-8c3f-4b3e63229456', 'AWS RDS 开启次要版本升级', 1, 'LowRisk', '确保 RDS 实例启用了次要版本升级。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  确保 RDS 实例启用了次要版本升级。开启视为”合规“，否则视为”不合规“。\n  #  返回：返回没有开启的RDS实例。\n  - name: aws-rds-instance-minor-version-upgrade-enabled\n    resource: aws.rds\n    filters:\n      - AutoMinorVersionUpgrade: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('28619f85-9885-4d2b-897a-d22964b22b88', 'AWS RDS 与cloudwatch集成', 1, 'MediumRisk', '检查RDS实例是否与CloudWatch Logs集成。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查RDS实例是否与CloudWatch Logs集成。开启视为”合规“，否则视为”不合规“。\n  - name: aws-rds-instance-integration-cloudwatch-logs\n    resource: aws.rds\n    filters:\n      - type: db-parameter\n        key: EnabledCloudwatchLogsExports[]\n        value: not-null', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('e2faa51d-af7c-4453-8cb4-30b58e9de23b', 'AWS RDS 开启增强监控检测', 1, 'LowRisk', '检查RDS实例是否启用了增强监控。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查RDS实例是否启用了增强监控。开启视为”合规“，否则视为”不合规“。\n  - name: aws-rds-instance-enhanced-monitoring-enabled\n    resource: aws.rds\n    filters:\n      - type: db-parameter\n        key: EnhancedMonitoringResourceArn\n        value: not-null', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('fb1a0dc1-99a8-4b34-84f4-1df613c0fa0b', 'AWS RDS 引擎版本检测', 1, 'MediumRisk', '检查 RDS 是否使用 MariaDB、MySQL 和 PostgreSQL 支持的引擎版本。引擎版本不过期为”合规“，否则视为”不合规“。', 'policies:\n  #  检查 RDS 是否使用 MariaDB、MySQL 和 PostgreSQL 支持的引擎版本。引擎版本不过期为”合规“，否则视为”不合规“。\n  - name: aws-rds-instance-deprecated-engine-version\n    resource: aws.rds\n    filters:\n      - type: engine\n        key: Status\n        value: deprecated', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('7b112945-0f71-4725-9e61-ebc37942817e', 'AWS RDS 开启删除保护检测', 1, 'MediumRisk', '检查RDS实例是否启用了删除保护。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查RDS实例是否启用了删除保护。开启视为”合规“，否则视为”不合规“。\n  - name: aws-rds-instance-backup-enabled\n    resource: aws.rds\n    filters:\n      - DeletionProtection: true', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('162e07f0-e468-4b4f-a023-36029d4a530e', 'AWS RDS 开启备份检测', 1, 'MediumRisk', '检查RDS实例是否启用了备份。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查RDS实例是否启用了备份。开启视为”合规“，否则视为”不合规“。\n  - name: aws-rds-instance-backup-enabled\n    resource: aws.rds\n    filters:\n      - not:\n        - type: value\n          key: BackupRetentionPeriod\n          value: 0\n          op: lt', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('5d4aa27e-bead-4da0-9878-b8694d957518', 'AWS 存储桶有安全传输策略', 1, 'MediumRisk', '检查 S3 存储桶是否具有安全传输策略。有的话视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查 S3 存储桶是否具有安全传输策略。有的话视为”合规“，否则视为”不合规“。\n  #  返回：返回 没有安全传输策略的 S3 存储桶。\n  - name: aws-s3-bucket-public-write-acl\n    resource: aws.s3\n    filters:\n    - or:\n      - type: missing-statement\n        statement_ids:\n          - RequiredEncryptedPutObject\n      - type: has-statement\n        statements:\n          - Effect: \"Deny\"\n            Action: \"s3:PutObject\"\n            Principal:\n              AWS: \"*\"\n            Condition:\n              Bool:\n                \"aws:SecureTransport\": false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('904dcef3-fae3-423b-b5ed-98bb41711b1a', 'AWS 存储桶是否启用了默认加密检测', 1, 'MediumRisk', '检查S3 bucket 是否开启了默认加密。开启视为”合规“，否则视为”不合规“。', 'policies:\n  #  检查S3 bucket 是否开启了默认加密。开启视为”合规“，否则视为”不合规“。\n  - name: aws-s3-bucket-default-encryption\n    resource: aws.s3\n    filters:\n      - type: bucket-encryption\n        state: False', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
INSERT INTO `rule` (`id`, `name`, `status`, `severity`, `description`, `script`, `parameter`, `plugin_id`, `plugin_name`, `plugin_icon`, `last_modified`, `flag`, `scan_type`, `suggestion`, `xpack_tag`) VALUES ('3ed456fe-9094-450e-8948-a0505b928760', 'AWS EBS 默认加密检测', 1, 'MediumRisk', '检查 EBS 默认加密是否已激活，开启视为”合规“，否则视为”不合规“', 'policies:\n  #  检查 EBS 默认加密是否已激活，开启视为”合规“，否则视为”不合规“\n  - name: aws-EC2-ebs-default-encryption\n    resource: aws.account\n    filters:\n    - type: default-ebs-encryption\n      state: false', '[]', 'hummer-aws-plugin', 'Amazon Web Services', 'aws.png', concat(unix_timestamp(now()), '004'), 1, 'custodian', NULL, 0);
