and t.id = #{request.id}
and LOWER(t.name) like CONCAT('%', LOWER(#{request.name}),'%')
and LOWER(t.image_url) like CONCAT('%', LOWER(#{request.imageUrl}),'%')
and t.sbom_version_id = #{request.sbomVersionId}
ORDER BY t.update_time desc

