mapper.select:
select
t.tag_key as sourceId,
'tag' as ruleType,
t.tag_name as ruleName,
t.tag_name as ruleDesc,

    (

        select id from cloud_account where id = #{accountId}


        select id from cloud_account where id = #{accountId}


        select id from server where id = #{accountId}


        select id from cloud_native where id = #{accountId}


        select id from cloud_native_config where id = #{accountId}


        select id from image where id = #{accountId}


        select id from code where id = #{accountId}


        select id from file_system where id = #{accountId}

    ) as accountId,


    (

        select name from cloud_account where id = #{accountId}


        select name from cloud_account where id = #{accountId}


        select name from server where id = #{accountId}


        select name from cloud_native where id = #{accountId}


        select name from cloud_native_config where id = #{accountId}


        select name from image where id = #{accountId}


        select name from code where id = #{accountId}


        select name from file_system where id = #{accountId}

    ) as accountName,


    (

        'cloudAccount'


        'vulnAccount'


        'serverAccount'


        'k8sAccount'


        'configAccount'


        'imageAccount'


        'codeAccount'


        'fsAccount'

) as accountType,

'tag.png' as icon,
'HighRisk' as severity
from rule_tag t


        AND t.tag_name like CONCAT('%', #{ruleName},'%')

select
t.id as sourceId,
'group' as ruleType,
t.name as ruleName,
t.description as ruleDesc,

    (

        select id from cloud_account where id = #{accountId}


        select id from cloud_account where id = #{accountId}


        select id from server where id = #{accountId}


        select id from cloud_native where id = #{accountId}


        select id from cloud_native_config where id = #{accountId}


        select id from image where id = #{accountId}


        select id from code where id = #{accountId}


        select id from file_system where id = #{accountId}

    ) as accountId,


    (

        select name from cloud_account where id = #{accountId}


        select name from cloud_account where id = #{accountId}


        select name from server where id = #{accountId}


        select name from cloud_native where id = #{accountId}


        select name from cloud_native_config where id = #{accountId}


        select name from image where id = #{accountId}


        select name from code where id = #{accountId}


        select name from file_system where id = #{accountId}

    ) as accountName,


    (

        'cloudAccount'


        'vulnAccount'


        'serverAccount'


        'k8sAccount'


        'configAccount'


        'imageAccount'


        'codeAccount'


        'fsAccount'

    ) as accountType,

'group.png' as icon,
'HighRisk' as severity
from rule_group t
left join cloud_account t1 on t.plugin_id = t1.plugin_id


        AND t.name like CONCAT('%', #{ruleName},'%')


        AND t1.id = #{accountId}

SELECT t.*, 'server.png' as pluginIcon, t1.tag_key as tagKey, t2.tag_name as tagName FROM server_rule t left join rule_tag_mapping t1 on t1.rule_id = t.id left join rule_tag t2 on t1.tag_key = t2.tag_key
AND t.id = #{sourceId}
ORDER BY t.last_modified desc

SELECT t.*, 'k8s.png' as pluginIcon, t1.tag_key as tagKey, t2.tag_name as tagName FROM cloud_native_rule t left join rule_tag_mapping t1 on t1.rule_id = t.id left join rule_tag t2 on t1.tag_key = t2.tag_key
AND t.id = #{sourceId}
ORDER BY t.last_modified desc

SELECT t.*, 'yaml.png' as pluginIcon, t1.tag_key as tagKey, t2.tag_name as tagName FROM cloud_native_config_rule t left join rule_tag_mapping t1 on t1.rule_id = t.id left join rule_tag t2 on t1.tag_key = t2.tag_key
AND t.id = #{sourceId}
ORDER BY t.last_modified desc

SELECT t.*, 'docker.png' as pluginIcon, t1.tag_key as tagKey, t2.tag_name as tagName FROM image_rule t left join rule_tag_mapping t1 on t1.rule_id = t.id left join rule_tag t2 on t1.tag_key = t2.tag_key
AND t.id = #{sourceId}
ORDER BY t.last_modified desc

SELECT t.*, 'code.png' as pluginIcon, t1.tag_key as tagKey, t2.tag_name as tagName FROM code_rule t left join rule_tag_mapping t1 on t1.rule_id = t.id left join rule_tag t2 on t1.tag_key = t2.tag_key
AND t.id = #{sourceId}
ORDER BY t.last_modified desc

SELECT t.*, 'fs.png' as pluginIcon, t1.tag_key as tagKey, t2.tag_name as tagName FROM file_system_rule t left join rule_tag_mapping t1 on t1.rule_id = t.id left join rule_tag t2 on t1.tag_key = t2.tag_key
AND t.id = #{sourceId}
ORDER BY t.last_modified desc

SELECT DISTINCT t2.id, t2.name, t2.status, t2.severity, t2.description, t2.parameter, t2.last_modified as lastModified, t2.flag, t2.script FROM rule_group t left join rule_group_mapping t1 on t.id = t1.group_id join rule t2 on t1.rule_id = t2.id
AND t.id = #{sourceId}
UNION ALL
SELECT DISTINCT t2.id, t2.name, t2.status, t2.severity, t2.description, t2.parameter, t2.last_modified as lastModified, t2.flag, t2.script FROM rule_group t left join rule_group_mapping t1 on t.id = t1.group_id join server_rule t2 on t1.rule_id = t2.id
AND t.id = #{sourceId}
UNION ALL
SELECT DISTINCT t2.id, t2.name, t2.status, t2.severity, t2.description, t2.parameter, t2.last_modified as lastModified, t2.flag, t2.script FROM rule_group t left join rule_group_mapping t1 on t.id = t1.group_id join image_rule t2 on t1.rule_id = t2.id
AND t.id = #{sourceId}
UNION ALL
SELECT DISTINCT t2.id, t2.name, t2.status, t2.severity, t2.description, t2.parameter, t2.last_modified as lastModified, t2.flag, t2.script FROM rule_group t left join rule_group_mapping t1 on t.id = t1.group_id join code_rule t2 on t1.rule_id = t2.id
AND t.id = #{sourceId}
SELECT DISTINCT t2.id, t2.name, t2.status, t2.severity, t2.description, t2.parameter, t2.last_modified as lastModified, t2.flag, t2.script FROM rule_group t left join rule_group_mapping t1 on t.id = t1.group_id join file_system_rule t2 on t1.rule_id = t2.id
AND t.id = #{sourceId}

SELECT t.id, concat(t.task_name, ' ', t.resource_types, ' | ', t.rule_tags) as name, t.description as 'desc', t.status, t.severity, t.plugin_icon as icon FROM history_cloud_task t WHERE t.id = #{resource_id}
UNION ALL
SELECT t.id, concat(t.task_name, ' ', t.resource_types) as name, t.description as 'desc', t.status, t.severity, t.plugin_icon as icon FROM history_vuln_task t WHERE t.id = #{resource_id}
UNION ALL
SELECT t.id, concat(t.server_name, ' ', t.ip, ':', t.port) as name, t.rule_desc as 'desc', t.result_status as status, t.severity, t.plugin_icon as icon FROM history_server_result t WHERE t.id = #{resource_id}
UNION ALL
SELECT t.id, concat(t.rule_name, ' | ' , t.name) as name, t.rule_desc as 'desc', t.result_status as status, t.severity, 'docker.png' as icon FROM history_image_result t WHERE t.id = #{resource_id}
UNION ALL
SELECT t.id, t.name as name, t.rule_desc as 'desc', t.result_status as status, t.severity, 'code.png' as icon FROM history_code_result t WHERE t.id = #{resource_id}
UNION ALL
SELECT t.id, t.name as name, t.rule_desc as 'desc', t.result_status as status, t.severity, 'fs.png' as icon FROM history_file_system_result t WHERE t.id = #{resource_id}

SELECT t.*
FROM task_item_resource_log t
WHERE t.task_item_resource_id = #{id}

SELECT t.*
FROM task t
WHERE t.id = #{id}

SELECT t.*
FROM image_result_item t
WHERE t.result_id = #{id}
