mapper.select:
select access_date as 'accessDate',count(*) as 'times' from (
    SELECT FROM_UNIXTIME(event_time/1000 ,'%Y-%m-%d') as access_date FROM cloud_event
    where
      source_ip_address = #{ip}
      and event_time between UNIX_TIMESTAMP(str_to_date(#{startDate},'%Y-%m-%d') )*1000 and UNIX_TIMESTAMP(str_to_date(#{endDate},'%Y-%m-%d') )*1000+24*60*1000
    ) a group by access_date
