sql.if:
and #{${condition}.ruleTag.value}


        in (select tag_key from rule_tag_mapping where rule_id = t.rule_id)


        not in (select tag_key from rule_tag_mapping where rule_id = t.rule_id)

mapper.select:
SELECT t.*, t1.file_name as fileName, t1.size as size, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'CRITICAL') as critical, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'HIGH') as high, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'MEDIUM') as medium, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'LOW') as low, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'UNKNOWN') as unknown FROM file_system_result t LEFT JOIN file_system t1 on t.fs_id = t1.id left join user on t.apply_user = user.id
and t.id = #{request.id}
and LOWER(t.name) like CONCAT('%', LOWER(#{request.name}),'%')
and LOWER(t1.file_name) like CONCAT('%', LOWER(#{request.fileName}),'%')
and t.sbom_version_id = #{request.sbomVersionId}
ORDER BY t.update_time desc

SELECT t.*, t1.file_name as fileName, t1.size as size, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'CRITICAL') as critical, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'HIGH') as high, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'MEDIUM') as medium, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'LOW') as low, (select count(*) from file_system_result_item tj where tj.result_id = t.id and severity = 'UNKNOWN') as unknown FROM history_file_system_result t LEFT JOIN file_system t1 on t.fs_id = t1.id
AND t.id = #{id}
AND t.fs_id = #{fsId}
AND t.sbom_version_id = #{sbomVersionId}
AND LOWER(t.name) like CONCAT('%',#{name},'%')
ORDER BY t.update_time desc
