sql.if:
and #{${condition}.ruleTag.value}


        in (select tag_key from rule_tag_mapping where rule_id = t.rule_id)


        not in (select tag_key from rule_tag_mapping where rule_id = t.rule_id)

mapper.select:
SELECT t.*, t1.tag_key as tagKey, t2.tag_name as tagName, u.name as user, sg.name as groupName, t3.script as rule FROM server_result t left join rule_tag_mapping t1 on t1.rule_id = t.rule_id left join rule_tag t2 on t1.tag_key = t2.tag_key left join server_rule t3 on t.rule_id = t3.id left join user u on t.apply_user = u.id left join server s on s.id = t.server_id left join server_group sg on sg.id = s.server_group_id
t.server_id = #{id}
ORDER BY FIELD(t.result_status, 'APPROVED', 'FINISHED', 'ERROR'), t.is_severity, t.ip, FIELD(t.severity, 'HighRisk', 'MediumRisk', 'LowRisk'), t.server_name, t.update_time desc

SELECT t.*, t1.tag_key as tagKey, t2.tag_name as tagName, u.name as user, sg.name as groupName, t3.script as rule FROM history_server_result t left join rule_tag_mapping t1 on t1.rule_id = t.rule_id left join rule_tag t2 on t1.tag_key = t2.tag_key left join server_rule t3 on t.rule_id = t3.id left join user u on t.apply_user = u.id left join server s on s.id = t.server_id left join server_group sg on sg.id = s.server_group_id
AND t.id = #{id}
AND t.server_id = #{serverId}
AND LOWER(t.name) like CONCAT('%',#{name},'%')
ORDER BY t.is_severity, t.ip, FIELD(t.severity, 'HighRisk', 'MediumRisk', 'LowRisk'), t.server_name, t.update_time desc
